mapper.update:
Update article
set title = #{title}, writedate = #{writedate}, score_count=#{score_count},
text=#{text}, traveldate=#{traveldate}, day=#{day}, img=#{img}
where articleno = #{articleno}

Update article
set score = (select AVG(score) from article_score where articleno = #{articleno})
where articleno = #{articleno}
